project_type.report_styles:
/* Global styles */

html {
    margin: 0;
    padding: 0;
    font-family: "Noto Sans", sans-serif;
    font-size: 11pt;
    overflow-wrap: break-word;
}

body {
    margin: 0;
    padding: 0;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    break-inside: avoid;
    break-after: avoid;
}

h1 {
    font-size: 20pt;
}

h2 {
    font-size: 16pt;
}

h3 {
    font-size: 14pt;
}

table {
    width: 100%;
}

table,
th,
td {
    border: 1px solid black;
    border-collapse: collapse;
}

th {
    font-weight: bold;
}

th,
td {
    padding: 0.3em;
}

tr {
    break-inside: avoid;
}

figure {
    break-inside: avoid;
    text-align: center;
    margin-left: 0;
    margin-right: 0;
}

img {
    max-width: 100%;
}

a {
    text-decoration: none;
}

li::marker {
    font-family: "Noto Sans", sans-serif;
}

figcaption {
    font-weight: bold;
}

.link-none {
    color: inherit;
    text-decoration: none;
}

.location-ul {
    margin: 0px;
    margin-bottom:0.2em;
}

.finding-risk-critical {
    color: #E83221;
    font-weight: bold;
}

.finding-risk-high {
    color: #FF9300;
    font-weight: bold;
}

.finding-risk-medium {
    color: #FFDA00;
    font-weight: bold;
}

.finding-risk-low {
    color: #4285F5;
    font-weight: bold;
}

.finding-risk-info {
    color: #00AE51;
    font-weight: bold;
}

.finding-status-open {
    color: #E83221;
    font-weight: bold;
}

.finding-status-partial {
    color: #FF9300;
    font-weight: bold;
}

.finding-status-resolved,
.finding-status-accepted {
    color: #00AE51;
    font-weight: bold;
}

.finding-status-changed {
    color: #4285F5;
    font-weight: bold;
}

.highlight {
    color: #303840;
}

/* PDF-specific global styles */
@page {
    size: A4 portrait;
    margin: 35mm 26mm 26mm 26mm;

    @top-center {
        content: element(header);
    }

    @bottom-right-corner {
        /* Page number */
        content: counter(page) " / " counter(pages);
        font-size: 9pt;
    }
}

@page: first {
    @top-center {
        content: "";
    }
    @bottom-right-corner {
        content: "";
    }
}


/* page header */
#header {
    position: running(header);
    height: 20mm;
    width: 180mm;
    background-color: #ABABAB;
    padding: 5mm 15mm 5mm 15mm;
    margin-top: -5mm;
}

#header-logo {
    width: 12%;
    display: inline-block;
    margin-top: -3mm;
}

#header-title {
    display: inline-block;
    position: absolute;
    top: 5mm;
    left: 40mm;
    text-align: right;
    align-self: center;
    color: #303840;
    font-size: 18pt;
}

#header-imprint {
    width: 64%;
    display: inline-block;
    position: absolute;
    top: 3mm;
    right: 20mm;
    text-align: right;
    align-self: center;
    color: #ffffff;
    font-size: 9pt;
    margin-top: 0;
}

#page-cover {
    margin: -35mm -26mm 10mm -26mm;
}

#page-cover-background {
    width: 100%;
    height: 120mm;
    overflow: hidden;
    background-color: black;
}

#page-cover-background-img {
    width: 100%;
    transform: scale(1.16) translateY(-11%);
    opacity: 0.7;
}

#page-cover-logo {
    position: absolute;
    top: -30mm;
    left: -7mm;
    width: 50%;
}

#page-cover-logo-text {
    position: absolute;
    top: -12mm;
    left: 4mm;
    color: #ffffff;
}

#page-cover-infobox {
    position: absolute;
    top: 30mm;
    left: -1mm;
    width: 150mm;
    color: #ffffff;
}

#page-cover-title {
    font-size: 18pt;
    font-weight: normal;
    margin-top: 0;
    margin-bottom: 0.3em;
}

#page-cover-customer {
    position: absolute;
    top: 43mm;
    left: -1mm;
    width: 150mm;
    color: #ffffff;
}

#page-cover-contact {
    position: absolute;
    top: 46mm;
    right: 4mm;
    text-align: right;
    color: #ffffff;
}

/* table of contents */
#toc a {
    color: black;
}

#toc a::after {
    font-weight: normal;
    content: " " target-counter(attr(href), page);
    float: right;
}

#toc ul {
    list-style: none;
    padding: 0;
}

#toc .level-1 {
    margin-top: 0.7em;
    font-weight: bold;
}

#toc .level-2 {
    padding-left: 1.5em;
    margin-top: 0.35em;
    font-weight: normal;
}

#toc .level-3 {
    padding-left: 3em;
    margin-top: 0.25em;
    font-weight: normal;
}

#findings-list .markdown h1 {
    font-size: 13pt;
    font-weight: bold;
}

#findings-list .markdown h2 {
    font-size: 12pt;
    font-weight: bold;
}

#findings-list .markdown h3 {
    font-size: 11pt;
    font-weight: bold;
}

.markdown-inline, .markdown-inline > *:first-child {
    display: inline;
}

.markdown code {
  background-color: whitesmoke;
  padding: 0.2em;
}
